mapper.select:
select
admin_user_id, login_user_name, login_password, nick_name, locked
from tb_user_admin


        and admin_user_id = #{adminUserId}


        and login_user_name = #{loginUserName}


        and login_password = #{loginPassword}


        and nick_name = #{nickName}


        and locked = #{locked}

select count(1)
from tb_user_admin


        and admin_user_id = #{adminUserId}


        and login_user_name = #{loginUserName}


        and login_password = #{loginPassword}


        and nick_name = #{nickName}


        and locked = #{locked}
